// select all divs and and a purple background
$("div").css("background-color", "purple");
// make all divs with highlight class 200px wide
$("div.highlight").css("width", "200px");
// add orange border to #third
$("div#third").css("border", "2px solid orange");
// bonus: select the first div and change font color to pink
$("div:first-of-type").css("color", "pink");